/* ==> Chachi Helpers <== */
/* nyteowldave.github.io  */

// Quick-n-Dirty Stack / Memo Update
function tuck() {
   sip.memo = sip.value;
   sip.stack = ( sip.stack || [] );
   sip.stack.push( sip.memo );
   return sip.stack.length;
}

// Like Tuck, but More Flexible
function submit( result ) {
   sip.memo = sip.value;
   sip.stack = ( sip.stack || [] );
   sip.stack.push( sip.memo );
   if ( "string" === typeof result ) {
        return result;
   }
   return sip.stack;
}

// Quick-n-Dirty Stack Download
function foreverStack( filename /* optional */ ) {
   f = ( filename || "chachi-stack.json" );
   rico( json( sip.stack ), f );
   console.log( "Downloaded", filename );
}

// Da Woiks ~ Backup Everything Imaginable
function backup( /* all state */ ) {
	const memo    = sip.memo;
	const value   = sip.value;
	const stack   = sip.stack;
	const tikey   = TiGG();
	const tidate  = "2025-AUG-15"; // Doesn't TiGG format these now?
	const author  = "NyteOwlDave"; // ^^^ Which Version ? ^^^
	const subject = location.href;
	const filename = location.hostname + "-" + tidate + ".json";
	const payload = { 
		heading : { author, subject, filename } ,
		tigg    : { tikey, tidate } ,
		sip     : { memo, value, stack }
	};
	const json = JSON.stringify( payload, null, 2 );
    rico( json, filename );
	return ( "Backup Complete : " + filename );
}

tuck();

