function tabSize( spaces=2 ) {
	return sip.style.tabSize = ( parseInt( spaces ) || 2 );
}

// Quick-n-Dirty Stack / Memo Update
function tuck() {
   sip.memo = sip.value;
   sip.stack = ( sip.stack || [] );
   sip.stack.push( sip.memo );
   return sip.stack.length;
}

// Like Tuck, but More Flexible
function submit( result ) {
   sip.memo = sip.value;
   sip.stack = ( sip.stack || [] );
   sip.stack.push( sip.memo );
   if ( "string" === typeof result ) {
        return result;
   }
   return sip.stack;
}

